import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [posts, setPosts] = useState([]);
  const [filteredPosts, setFilteredPosts] = useState([]);
  const [userIdFilter, setUserIdFilter] = useState('');
  const [titleFilter, setTitleFilter] = useState('');
  const [bodyFilter, setBodyFilter] = useState('');

  useEffect(() => {
    axios.get('https://jsonplaceholder.typicode.com/posts').then(response => {
      setPosts(response.data);
      setFilteredPosts(response.data);
    });
  }, []);

  useEffect(() => {
    let filtered = posts;

    if (userIdFilter) {
      filtered = filtered.filter(post => post.userId.toString() === userIdFilter);
    }

    if (titleFilter) {
      filtered = filtered.filter(post => post.title.includes(titleFilter));
    }

    if (bodyFilter) {
      filtered = filtered.filter(post => post.body.includes(bodyFilter));
    }

    setFilteredPosts(filtered);
  }, [userIdFilter, titleFilter, bodyFilter, posts]);

  return (
    <div className="container">
      <h1>Posts</h1>
      <div className="row">
        <input
          className="form-control col"
          placeholder="Filter by User ID"
          value={userIdFilter}
          onChange={e => setUserIdFilter(e.target.value)}
        />
        <input
          className="form-control col"
          placeholder="Filter by Title"
          value={titleFilter}
          onChange={e => setTitleFilter(e.target.value)}
        />
        <input
          className="form-control col"
          placeholder="Filter by Body"
          value={bodyFilter}
          onChange={e => setBodyFilter(e.target.value)}
        />
      </div>
      <div className="row">
        {filteredPosts.map(post => (
          <div key={post.id} className="col-md-6">
            <h2>{post.title}</h2>
            <p>{post.body}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
